        strArr[55] = "\u0ca4\u0cc1\u0c82\u0cac\u0cbe \u0cae\u0ca8\u0cb8\u0ccd\u0cb8\u0cc1 \u0c95\u0cbe\u0ca1\u0cbf\u0ca6 \u0cb8\u0cbe\u0cb2\u0cc1\u0c97\u0cb3\u0cc1\n-----------------------------------\n\u0ca8\u0cbf\u0ca8\u0ccd\u0ca8 \u0caf\u0cb6\u0cb8\u0ccd\u0cb8\u0cbf\u0ca8 \u0c95\u0cc1\u0cb0\u0cbf\u0ca4\u0cc1 \u0c97\u0cb0\u0ccd\u0cb5\u0caa\u0ca1\u0cac\u0cc7\u0ca1 \n\u0c9a\u0cc1\u0c95\u0ccd\u0c95\u0cbf\u0c97\u0cb3\u0cc1 \u0c9c\u0cbe\u0cb0\u0cbf \u0cac\u0cc0\u0cb3\u0cc1\u0cb5\u0cc1\u0ca6\u0ca8\u0ccd\u0ca8\u0cc1 \u0ca8\u0cbe\u0ca8\u0cc1 \u0c95\u0c82\u0ca1\u0cbf\u0ca6\u0ccd\u0ca6\u0cc7\u0ca8\u0cc6!\n\u0ca8\u0cbf\u0ca8\u0ccd\u0ca8 \u0c9c\u0ccd\u0c9e\u0cbe\u0ca8\u0ca6 \u0c86\u0cb3\u0cb5\u0ca8\u0ccd\u0ca8\u0cc1 \u0c95\u0cca\u0c82\u0ca1\u0cbe\u0ca1\u0cac\u0cc7\u0ca1\n\u0cb8\u0cbe\u0c97\u0cb0\u0c97\u0cb3\u0cc1 \u0cac\u0ca4\u0ccd\u0ca4\u0cbf\u0cb0\u0cc1\u0cb5\u0cc1\u0ca6\u0ca8\u0ccd\u0ca8\u0cc1  \n\u0ca8\u0cbe\u0ca8\u0cc1 \u0c95\u0c82\u0ca1\u0cbf\u0ca6\u0ccd\u0ca6\u0cc7\u0ca8\u0cc6!\n\u0ca8\u0cbf\u0ca8\u0ccd\u0ca8 \u0cb0\u0cc2\u0caa\u0ca6 \u0cac\u0c97\u0ccd\u0c97\u0cc6 \u0c85\u0cb9\u0c82\u0c95\u0cbe\u0cb0 \u0caa\u0ca1\u0cac\u0cc7\u0ca1\n\u0cae\u0cca\u0ca8\u0cbe\u0cb2\u0cbf\u0cb8\u0cbe \u0cae\u0ca3\u0ccd\u0ca3\u0cc1 \u0cb8\u0cc7\u0cb0\u0cbf\u0ca6\u0ccd\u0ca6\u0ca8\u0ccd\u0ca8\u0cc1 \u0ca8\u0cbe\u0ca8\u0cc1 \u0c95\u0c82\u0ca1\u0cbf\u0ca6\u0ccd\u0ca6\u0cc7\u0ca8\u0cc6!\n\u0c9c\u0cc0\u0cb5\u0ca8\u0ca6\u0cb2\u0ccd\u0cb2\u0cbf \u0c97\u0cc6\u0ca6\u0ccd\u0ca6\u0cc6\u0ca8\u0cc6\u0c82\u0ca6\u0cc1 \u0cac\u0cc0\u0c97\u0cac\u0cc7\u0ca1 \n\u0c85\u0cb2\u0cc6\u0c95\u0ccd\u0cb8\u0cbe\u0c82\u0ca1\u0cb0\u0ccd \u0cb6\u0cb0\u0ca3\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0ca8\u0ccd\u0ca8\u0cc1 \u0ca8\u0cbe\u0ca8\u0cc1 \u0c95\u0c82\u0ca1\u0cbf\u0ca6\u0ccd\u0ca6\u0cc7\u0ca8\u0cc6...\n";
