        strArr[91] = "\u0cb9\u0cc3\u0ca6\u0caf\u0c95\u0cc2\n\u0ca4\u0cc1\u0c9f\u0cbf\u0c97\u0cb3\u0cbf\u0c97\u0cc2\n\u0c85\u0ca6\u0cc6\u0cb7\u0ccd\u0c9f\u0cc1\n\u0caf\u0ccb\u0c9c\u0ca8\u0cc6\u0c97\u0cb3\n\u0c85\u0c82\u0ca4\u0cb0\u0cb5\u0ccb\n\u0c85\u0ca5\u0cb5\u0cbe\n\u0c85\u0ca6\u0cc6\u0cb7\u0ccd\u0c9f\u0cc6\u0ca4\u0ccd\u0ca4\u0cb0\u0ca6\n\u0c97\u0ccb\u0ca1\u0cc6\u0caf\u0cbf\u0ca6\u0cc6\u0caf\u0ccb\n\u0c85\u0ca5\u0cb5\u0cbe\n\u0c85\u0ca6\u0cbe\u0cb5\u0cbe\u0cb3\u0ca6\n\u0c95\u0c82\u0ca6\u0cb0\u0cb5\u0cbf\u0ca6\u0cc6\u0caf\u0ccb,\n\u0cae\u0ca8\u0ca6\u0cbe\u0cb3\u0ca6\n\u0cae\u0cbe\u0ca4\u0cc1\u0c97\u0cb3\u0cc1\n\u0ca4\u0cc1\u0c9f\u0cbf\u0caf\u0c82\u0c9a\u0cbf\u0c97\u0cc6\n\u0cac\u0cbe\u0cb0\u0ca6\u0cbe\u0c97\u0cbf\u0cb5\u0cc6,\n";
        strArr[92] = "\u0c85\u0cae\u0ccd\u0cae\u0cbe \u0c90 \u0cb2\u0cb5\u0ccd \u0caf\u0cc2. \u0caa\u0cc2\u0cb0\u0ccd\u0ca4\u0cbf \u0c93\u0ca6\u0cbf Frndsss\n\n\u0c88 \u0c9c\u0cc0\u0cb5\u0ca8\u0ca6\u0cb2\u0ccd\u0cb2\u0cbf \u0cae\u0cca\u0ca6\u0cb2\u0cc1 \u0ca4\u0cbe\u0caf\u0cbf \u0c85\u0cb3\u0ccd\u0ca4\u0cbe \u0c87\u0ca6\u0ccd\u0cb3\u0cc1\n\u0caf\u0cbe\u0c95\u0cc6 \u0c97\u0ccb\u0ca4\u0ccd\u0ca4\u0cbe.....?\n\u0ca4\u0ca8\u0ccd\u0ca8 \u0cae\u0c97\u0cc1 \u0c8a\u0c9f\u0cbe \u0cae\u0cbe\u0ca1\u0ccd\u0ca4\u0cbe \u0c87\u0cb2\u0ccd\u0cb2\u0cbe \u0c85\u0c82\u0ca4. \n\n\u0c87\u0cb5\u0cbe\u0c97\u0ccd\u0cb2\u0cc2 \u0ca4\u0cbe\u0caf\u0cbf \u0c85\u0cb3\u0ca4\u0ccd\u0ca4\u0cbe\u0ca8\u0cc6 \u0c87\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6.\n\u0caf\u0cbe\u0c95\u0cc6 \u0c97\u0ccb\u0ca4\u0ccd\u0ca4\u0cbe.....?\n\u0ca8\u0cbe\u0ca8\u0cc2 \u0c9c\u0ca8\u0ccd\u0cae \u0c95\u0cca\u0c9f\u0ccd\u0c9f\u0cc1,\n\u0cac\u0cc6\u0cb3\u0cb8\u0cbf\u0ca6 \u0cae\u0c97 \u0ca8\u0ca8\u0c97\u0cc6 \u0c8a\u0c9f\u0cbe \u0c95\u0cca\u0ca1\u0ccd\u0ca4\u0cbe \u0c87\u0cb2\u0ccd\u0cb2 \u0c85\u0c82\u0ca4.\n\n\u0c92\u0c82\u0ca6\u0cc1 \u0c95\u0ccd\u0cb7\u0ca3 \u0caf\u0ccb\u0c9a\u0ca8\u0cc6 \u0cae\u0cbe\u0ca1\u0cbf \u0cab\u0ccd\u0cb0\u0cc6\u0ce6\u0ca1\u0ccd\u0cb8\u0ccd.\n\u0c88 \u0ca6\u0cc7\u0cb9 \u0ca4\u0cbe\u0caf\u0cbf \u0c95\u0cca\u0c9f\u0ccd\u0c9f \u0cac\u0cbf\u0c95\u0ccd\u0cb7\u0cc6,\n\u0c85\u0cb5\u0cb3\u0cc1 \u0cae\u0ca8\u0cb8\u0ccd\u0cb8\u0cc1 \u0cae\u0cbe\u0ca1\u0cbf \u0c9c\u0ca8\u0ccd\u0cae \u0c95\u0cca\u0c9f\u0ccd\u0c9f\u0cbf\u0ca6\u0c95\u0ccd\u0c95\u0cc6....\n\u0c88 \u0c9c\u0c97\u0ca4\u0ccd\u0ca4\u0cbf\u0ca8 \u0cae\u0cc1\u0c96 \u0ca8\u0ccb\u0ca1\u0cc1\u0ca4\u0ccd\u0ca4\u0cbf\u0ca6\u0ccd\u0ca6\u0cc7\u0cb5\u0cc6.\n\n\u0c85\u0cb5\u0cb3\u0cc1 \u0caf\u0cbe\u0cb5 \u0ca6\u0cc7\u0cb5\u0cb0\u0cbf\u0c97\u0cb3\u0cbf\u0c97\u0cbf\u0c82\u0ca4\u0cb2\u0cc2 \u0caf\u0cbe\u0cb5\u0cc2\u0ca6\u0cb0\u0cb2\u0ccd\u0cb2\u0cbf\u0caf\u0cc2 \u0c95\u0ca1\u0cbf\u0cae\u0cc6 \u0c87\u0cb2\u0ccd\u0cb2.\n\u0ca6\u0cc7\u0cb5\u0cb0\u0cc1 \u0ca4\u0ca1\u0cb5\u0cbe\u0c97\u0cbf \u0ca8\u0cae\u0ccd\u0cae\u0ca8\u0ccd\u0ca8 \u0ca8\u0ccb\u0ca1\u0ccd\u0ca4\u0cbe\u0ca8\u0cc6.\n\u0c86\u0ca6\u0cb0\u0cc6 \u0ca4\u0cbe\u0caf\u0cbf \u0cb9\u0cbe\u0c97\u0cb2\u0ccd\u0cb2.\n\u0c85\u0cae\u0ccd\u0cae\u0cbe \u0c85\u0c82\u0ca6 \u0ca4\u0c95\u0ccd\u0cb7\u0ca3 \u0c93\u0ca1\u0cbf \u0cac\u0cb0\u0cc1\u0ca4\u0ccd\u0ca4\u0cbe\u0cb3\u0cc6. \n\u0cab\u0ccd\u0cb2\u0cc0\u0cb8\u0ccd \u0cab\u0ccd\u0cb0\u0cc6\u0ce6\u0ca1\u0ccd\u0cb8\u0ccd\n\u0cb6\u0cc7\u0cb0\u0ccd \u0cae\u0cbe\u0ca1\u0cbf \u0cab\u0ccd\u0cb2\u0cc0\u0cb8\u0ccd. \n\n\u0caf\u0cbe\u0c95\u0cc6\u0c82\u0ca6\u0cb0\u0cc6....?\n\u0c88 \u0cad\u0cc2\u0cae\u0cbf \u0cae\u0cc7\u0cb2\u0cc6 \u0caf\u0cbe\u0cb5 \u0ca4\u0cbe\u0caf\u0cbf\u0ca8\u0cc2 \u0c95\u0cc2\u0ca1 \"\u0c89\u0caa\u0cb5\u0cbe\u0cb8\" \u0c87\u0cb0\u0c95\u0cc2\u0ca1\u0ca6\u0cc1. \n\u0c85\u0ca6\u0cca\u0c82\u0ca6\u0cc6 \u0ca8\u0ca8\u0ccd\u0ca8 \u0c95\u0ccb\u0cb0\u0cbf\u0c95\u0cc6. \n\u0cab\u0ccd\u0cb2\u0cc0\u0cb8\u0ccd \u0ca4\u0cbe\u0caf\u0cbf\u0ca8\u0cc7 \u0ca6\u0cc7\u0cb5\u0cb0\u0cc2...\n\u0ca4\u0cbe\u0caf\u0cbf\u0ca8\u0cc7 \u0cb8\u0cb0\u0ccd\u0cb5\u0cb8\u0ccd\u0cb5....\n\u0ca4\u0cbe\u0caf\u0cbf\u0ca8\u0cc7 \u0c9c\u0c97\u0ca4\u0ccd\u0ca4\u0cc1....\n\u0c85\u0ca8\u0ccd\u0ca8\u0ccb\u0cb0\u0cbf\u0c97\u0cc6 \u0ca4\u0cbe\u0caf\u0cbf \u0caa\u0ccd\u0cb0\u0cc0\u0ca4\u0cbf \u0c97\u0cca\u0ca4\u0ccd\u0ca4\u0cbf\u0cb0\u0ca4\u0ccd\u0ca4\u0cc6.....\n\u0ca8\u0cbf\u0cae\u0ccd\u0cae \u0ca8\u0cbf\u0cae\u0ccd\u0cae \u0ca4\u0cbe\u0caf\u0cbf \u0cae\u0cc7\u0cb2\u0cc6 \u0caa\u0ccd\u0cb0\u0cc0\u0ca4\u0cbf \u0c87\u0ca6\u0ccd\u0cb0\u0cc7 \u0c96\u0c82\u0ca1\u0cbf\u0ca4\u0cb5\u0cbe\u0c97\u0cbf \u0ca8\u0cc0\u0cb5\u0cc2 \u0cb6\u0cc7\u0cb0\u0ccd \u0cae\u0cbe\u0ca1\u0ccd\u0ca4\u0cbf\u0cb0\u0cbe.......";
        strArr[93] = "\u0caa\u0ca4\u0ccd\u0ca8\u0cbf\u0caf \u0c95\u0cc1\u0cb0\u0cbf\u0ca4\u0cc1 \u0c95\u0cc6\u0cb2\u0cb5\u0cc1 \u0cb5\u0cbf\u0cb7\u0caf\u0c97\u0cb3\u0cc1\n\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\u2018\n1. \u0c85\u0cb5\u0cb3\u0cc1 \u0c8e\u0cb2\u0ccd\u0cb2\u0cb5\u0cc1 \u0ca4\u0cbf\u0cb3\u0cbf\u0ca6 \u0caa\u0cb0\u0cbf\u0caa\u0cc2\u0cb0\u0ccd\u0ca3\u0ca4\u0cc6 \u0cb9\u0cca\u0c82\u0ca6\u0cbf\u0ca6\u0cb5\u0cb3\u0cb2\u0ccd\u0cb2,  \u0c85\u0cb5\u0cb3\u0cbf\u0c97\u0cc6 \u0c95\u0ccd\u0cb7\u0cae\u0cbf\u0cb8\u0cbf\u0cb0\u0cbf.\n\n2. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0ccd\u0ca8 \u0c86\u0cb8\u0ccd\u0ca4\u0cbf\u0caf \u0c86\u0cb8\u0ccd\u0ca4\u0cbf\u0caf\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6, \u0c85\u0ca6\u0ca8\u0ccd\u0ca8\u0cc1 \u0c92\u0ca1\u0cc6\u0caf\u0cac\u0cc7\u0ca1\u0cbf\u0cb0\u0cbf.\n\n3. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0c97\u0cc6 \u0ca6\u0cca\u0cb0\u0cc6\u0ca4 \u0cac\u0cb9\u0cc1\u0cae\u0cbe\u0ca8\u0cb5\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6, \u0cb8\u0ccd\u0cb5\u0cc0\u0c95\u0cb0\u0cbf\u0cb8\u0cbf\u0cb0\u0cbf.\n\n4. \u0c85\u0cb5\u0cb3\u0cc1 \u0cac\u0cc6\u0cb2\u0cc6\u0cac\u0cbe\u0cb3\u0cc1\u0cb5 \u0cae\u0cc1\u0ca4\u0ccd\u0ca4\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6, \u0c85\u0ca6\u0ca8\u0ccd\u0ca8\u0cc1 \u0cae\u0cc6\u0ca6\u0cc1\u0cb5\u0cbe\u0c97\u0cbf\u0cb8\u0cbf\u0cb0\u0cbf.\n\n5. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0c97\u0cc6 \u0c85\u0ca4\u0ccd\u0caf\u0c82\u0ca4 \u0ca8\u0cbf\u0c95\u0c9f\u0cb3\u0cbe\u0ca6 \u0c9c\u0cca\u0ca4\u0cc6\u0c97\u0cbe\u0cb0\u0ccd\u0ca4\u0cbf\u0caf\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6, \u0c9c\u0c97\u0cb3\u0cb5\u0cbe\u0ca1\u0ca6\u0cbf\u0cb0\u0cbf.\n\n6. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0c97\u0cc6 \u0cb8\u0c82\u0ca4\u0ccb\u0cb7\u0c97\u0cca\u0cb3\u0ccd\u0cb3\u0cb2\u0cbf\u0c95\u0ccd\u0c95\u0cbf\u0cb0\u0cc1\u0cb5\u0cb5\u0cb3\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6, \u0c85\u0cb5\u0cb3\u0ca8\u0ccd\u0ca8\u0cc2 \u0cb8\u0c82\u0ca4\u0ccb\u0cb7\u0c97\u0cca\u0cb3\u0cbf\u0cb8\u0cbf\u0cb0\u0cbf.\n\n7. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0ccd\u0ca8 \u0c9c\u0cc0\u0cb5\u0ca8\u0ca6 \u0cad\u0cbe\u0c97\u0cb5\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6, \u0ca8\u0ccb\u0cb5\u0cbf\u0cb8\u0ca6\u0cbf\u0cb0\u0cbf.\n\n8. \u0c85\u0cb5\u0cb3\u0cc1 \u0cb2\u0cc8\u0c82\u0c97\u0cbf\u0c95 \u0c95\u0ccd\u0cb0\u0cbf\u0caf\u0cc7\u0c97\u0cc6  \u0cae\u0cbe\u0ca4\u0ccd\u0cb0\u0cb5\u0cbf\u0cb0\u0cc1\u0cb5\u0cb5\u0cb3\u0cb2\u0ccd\u0cb2, \u0cb8\u0c82\u0ca4\u0ccb\u0cb7 \u0cae\u0ca4\u0ccd\u0ca4\u0cc1 \u0caa\u0ccd\u0cb0\u0caf\u0cbe\u0cb8\u0ca6 \u0cb8\u0c82\u0ca6\u0cb0\u0ccd\u0cad\u0ca6\u0cb2\u0ccd\u0cb2\u0cc2 \u0c9c\u0cca\u0ca4\u0cc6\u0c97\u0cbf\u0cb0\u0cbf.\n\n9. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0ccd\u0ca8 \u0cb6\u0ca4\u0ccd\u0cb0\u0cc1\u0cb5\u0cb2\u0ccd\u0cb2, \u0caa\u0ccd\u0cb0\u0ccb\u0ca4\u0ccd\u0cb8\u0cbe\u0cb9 \u0ca8\u0cc0\u0ca1\u0cbf\u0cb0\u0cbf.\n\n10. \u0c85\u0cb5\u0cb3\u0cca\u0cac\u0ccd\u0cac\u0cb3\u0cc1 \u0cae\u0cb9\u0cbf\u0cb3\u0cc6\u0caf\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6, \u0c97\u0ccc\u0cb0\u0cb5\u0cbf\u0cb8\u0cbf\u0cb0\u0cbf.\n\n11. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0c97\u0cbe\u0c97\u0cbf \u0cae\u0cbe\u0ca4\u0ccd\u0cb0\u0cb5\u0cbf\u0cb0\u0cc1\u0cb5\u0cb5\u0cb3\u0cc1, \u0ca4\u0cbe\u0cb0\u0ca4\u0cae\u0ccd\u0caf \u0ca4\u0ccb\u0cb0\u0ca6\u0cbf\u0cb0\u0cbf.\n\n12. \u0c85\u0cb5\u0cb3\u0cca\u0cac\u0ccd\u0cac\u0cb3\u0cc1 \u0cb0\u0cbe\u0ca3\u0cbf\u0caf\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6, \u0c89\u0ca4\u0ccd\u0cb8\u0cbe\u0cb9 \u0c97\u0cca\u0cb3\u0ccd\u0cb3\u0cbf\u0cb0\u0cbf.\n\n13. \u0c85\u0cb5\u0cb3\u0cc1 \u0c89\u0caa\u0ccd\u0caa\u0cc1 \u0c9a\u0cc0\u0cb2\u0cb5\u0cb2\u0ccd\u0cb2, \u0cb9\u0cca\u0ca1\u0cc6\u0caf\u0ca6\u0cbf\u0cb0\u0cbf\n\n14. \u0c85\u0cb5\u0cb3\u0cca\u0cac\u0ccd\u0cac \u0caa\u0cb0\u0cbf\u0cb9\u0cbe\u0cb8\u0ccd\u0caf\u0c97\u0cbe\u0cb0\u0ccd\u0ca4\u0cbf\u0caf\u0cb2\u0ccd\u0cb2, \n\u0caa\u0cb0\u0cbf\u0cb9\u0cbe\u0cb8\u0ccd\u0caf \u0cae\u0cbe\u0ca1\u0ca6\u0cbf\u0cb0\u0cbf\n\n17. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0ccd\u0ca8\u0ca8\u0ccd\u0ca8\u0cc1 \u0cae\u0cbe\u0ca4\u0ccd\u0cb0 \u0caa\u0ccd\u0cb0\u0cc0\u0ca4\u0cbf\u0cb8\u0cc1\u0cb5\u0cb5\u0cb3\u0cc1, \u0caa\u0ccd\u0cb0\u0cb6\u0c82\u0cb8\u0cbf\u0cb8\u0cbf\u0cb0\u0cbf\n\n18. \u0c85\u0cb5\u0cb3\u0cc1 \u0cae\u0cc2\u0cb0\u0ccd\u0c96\u0cb3\u0cb2\u0ccd\u0cb2, \u0c85\u0cb5\u0cb3\u0cc1 \u0cb9\u0cc7\u0cb3\u0cc1\u0cb5 \u0cb8\u0ca6\u0cc1\u0caa\u0ca6\u0cc7\u0cb6\u0cb5\u0ca8\u0ccd\u0ca8\u0cc1 \u0c86\u0cb2\u0cbf\u0cb8\u0cbf\u0cb0\u0cbf.\n\n19. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0ccd\u0ca8 \u0c9c\u0cb5\u0cbe\u0cac\u0ccd\u0ca6\u0cbe\u0cb0\u0cbf\u0caf\u0cc1\u0cb3\u0ccd\u0cb3\u0cb5\u0cb3\u0cc1, \u0c85\u0ca6\u0ca8\u0ccd\u0ca8\u0cc1 \u0ca8\u0cc6\u0cb0\u0cb5\u0cc7\u0cb0\u0cbf\u0cb8\u0cbf\u0cb0\u0cbf.\n\n20. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0c97\u0cc6 \u0cae\u0cc1\u0c96\u0ccd\u0caf, \u0c86 \u0cb8\u0ccd\u0ca5\u0cbe\u0ca8\u0cb5\u0ca8\u0ccd\u0ca8\u0cc1 \u0caf\u0cbe\u0cb0\u0cbf\u0c97\u0cc2 \u0ca8\u0cc0\u0ca1\u0ca6\u0cbf\u0cb0\u0cbf.\n\n21. \u0c85\u0cb5\u0cb3\u0cbf\u0c97\u0cc6 \u0ca8\u0cbf\u0ca8\u0ccd\u0ca8 \u0cb8\u0cb9\u0cbe\u0caf\u0ca6 \u0c85\u0c97\u0ca4\u0ccd\u0caf\u0cb5\u0cbf\u0ca6\u0cc6, \u0cb8\u0cb9\u0cbe\u0caf \u0cae\u0cbe\u0ca1\u0cbf\u0cb0\u0cbf.\n\n22. \u0c85\u0cb5\u0cb3\u0cc1 \u0ca8\u0cbf\u0ca8\u0ccd\u0ca8 \u0c95\u0cbf\u0cb0\u0cc0\u0c9f\u0cb5\u0cbe\u0c97\u0cbf\u0ca6\u0ccd\u0ca6\u0cbe\u0cb3\u0cc6, \u0c85\u0cb5\u0cb3\u0ca8\u0ccd\u0ca8\u0cc1 \u0ca4\u0cca\u0cb0\u0cc6\u0caf\u0ca6\u0cbf\u0cb0\u0cbf.";
        strArr[94] = "\u0c85\u0ca6\u0cc6\u0cb7\u0ccd\u0c9f\u0ccb \u0c95\u0cc8\u0c97\u0cc6 \u0cb8\u0cbf\u0c97\u0ca6 \u0c95\u0ca8\u0cb8\u0cc1\u0c97\u0cb3\u0ca8\u0ccd\u0ca8\u0cc1 \n\u0ca4\u0ca8\u0ccd\u0ca8\u0cca\u0cb3\u0c97\u0cc6 \u0c85\u0ca1\u0c97\u0cbf\u0cb8\u0cbf\u0c95\u0cca\u0c82\u0ca1\u0cbf\u0cb0\u0cc1\u0cb5\u0cc1\u0ca6\u0cb0\u0cbf\u0c82\u0ca6,\n\u0ca8\u0cbf\u0ca6\u0cbf\u0cb0\u0cc6\u0caf\u0cc2 \u0cb8\u0cb9 \u0c92\u0c82\u0ca6\u0cc1 \u0cb0\u0cc0\u0ca4\u0cbf\u0caf\u0cb2\u0ccd\u0cb2\u0cbf \u0ca8\u0cbf\u0ca7\u0cbf\u0caf\u0cc7...\n";
